       VSPGeom().Grid(MGLevel_).NodeList(i).DFiy_DX() = 0.;
       VSPGeom().Grid(MGLevel_).NodeList(i).DFiy_DY() = 0.;
       VSPGeom().Grid(MGLevel_).NodeList(i).DFiy_DZ() = 0.;
                                                      
       VSPGeom().Grid(MGLevel_).NodeList(i).DFoy_DX() = 0.;
