    double pFx_pSVecX;
    double pFy_pSVecY;
    double pFz_pSVecZ;             
    
    double pRVecX_pXc_TE;       
    double pRVecY_pYc_TE;       
    double pRVecZ_pZc_TE;       
    
    double pRVecX_pXc_LE;       
    double pRVecY_pYc_LE;       
    double pRVecZ_pZc_LE;    
                   
    double pMx_pFx;
    double pMx_pFy;
